frappe.ui.form.on('Delivery Note', {
    refresh(frm) {
        console.log(cur_frm.fields_dict['items'].grid.data.length)
        cur_frm.set_df_property('scan_barcode', 'hidden',1);
        frm.set_df_property('currency_and_price_list', 'hidden',1);
        frm.set_df_property('accounting_dimensions_section', 'hidden',1);

        if (frm.doc.__islocal && !frm.doc.is_return) {
    frm.clear_table("items");
}

        // Calculate totals when the form refreshes
        calculate_totals(frm);
    },
    custom_supplier_batch_no: function(frm) {
        fetch_and_append_batch(frm);
    },
    custom_scan_batch_no: function(frm, cdt, cdn) {
        // Check if the batch already exists in the items table
        const existingRow = frm.doc.items.find(d => d.batch_no === frm.doc.custom_scan_batch_no);
        if (existingRow) {
            // If the batch already exists, notify the user and clear the field
            frappe.msgprint(__("Batch already exists in the table"));
            frm.set_value("custom_scan_batch_no", "");
            frm.refresh_field("custom_scan_batch_no");
        }
        else {
            // If the batch does not exist, make a server call to get the batch details
            frappe.call({
                method: "mhr.utilis.get_item_batch",
                args: {
                    batch: frm.doc.custom_scan_batch_no
                },
                callback: function(r) {
                    if (r.message.error) {
                        // If there is an error in the response, possibly handle it here
                        return;
                    }
                    if (r.message) {
                        if((r.message.glue).toLowerCase() !== (frm.doc.custom_glue).toLowerCase() ||
                           (r.message.lusture).toLowerCase() !== (frm.doc.custom_lusture).toLowerCase() ||
                           (r.message.grade).toLowerCase() !== (frm.doc.custom_grade).toLowerCase() ||
                           (r.message.pulp).toLowerCase() !== (frm.doc.custom_pulp).toLowerCase() ||
                           (r.message.fsc).toLowerCase() !== (frm.doc.custom_fsc).toLowerCase()
                        ) { // Compare the lowercase values
                            frappe.msgprint(__("Parameters are not the same as the value in batch"));
                            frm.set_value("custom_scan_batch_no", "");
                            frm.refresh_field("custom_scan_batch_no");
                        }
                        else if(frm.doc.custom_cone && (r.message.cone) !== (frm.doc.custom_cone)){
                            frappe.msgprint(__("Parameters are not the same as the value in batch"));
                            frm.set_value("custom_scan_batch_no", "");
                            frm.refresh_field("custom_scan_batch_no");
                        }
                        else {
                            frm.add_child("items", {
                                item_code: r.message.item_code,
                                item_name: r.message.item_name,
                                qty: r.message.qty,
                                batch_no: r.message.batch_no,
                                uom: r.message.uom,
                                custom_cone: r.message.cone,
                                custom_supplier_batch_no: r.message.supplier_batch_no,
                                custom_container_no: r.message.container_no,
                                custom_lot_no: r.message.lot_no,
                                use_serial_batch_fields: 1
                            });
                            frm.doc.items.unshift(frm.doc.items.pop());
                            frm.refresh_field("items");
                            frm.set_value("custom_scan_batch_no", "");
                            frm.refresh_field("custom_scan_batch_no");
                            if (r.message.notes) {
                                frm.set_value("custom_notes", r.message.notes);
                            }

                            // Calculate totals after adding a new row
                            calculate_totals(frm);
                        }
                    }
                }
            });
        }
    }
});

// Listen for changes in the child table items
frappe.ui.form.on('Delivery Note Item', {
    items_add: function(frm, cdt, cdn) {
        calculate_totals(frm);
    },
    items_remove: function(frm, cdt, cdn) {
        calculate_totals(frm);
    },
    qty: function(frm, cdt, cdn) {
        calculate_totals(frm);
    },
    custom_cone: function(frm, cdt, cdn) {
        calculate_totals(frm);
    }
});

// Function to calculate total cone and total quantity
function calculate_totals(frm) {
    let total_cone = 0;
    let total_qty = 0;

    // Loop through each row in the items child table
    (frm.doc.items || []).forEach(function(row) {
        // Add the cone value of each row to total_cone
        total_cone += parseFloat(row.custom_cone || 0);

        // Add the qty value of each row to total_qty
        total_qty += parseFloat(row.qty || 0);
    });

    // Set the total cone and total qty in the main document
    frm.set_value('custom_total_cone', total_cone);
    frm.set_value('total_qty', total_qty);

    // Refresh the fields to show the updated values
    frm.refresh_field('custom_total_cone');
    frm.refresh_field('total_qty');
}

function fetch_and_append_batch(frm) {
    // Ensure both fields have values before making the call
    if (frm.doc.custom_supplier_batch_no && frm.doc.custom_container_no && frm.doc.custom_lot_no) {
        frappe.call({
            method: "mhr.utilis.get_delivery_note_batch",
            args: {
                lot_no: frm.doc.custom_lot_no,
                container_no: frm.doc.custom_container_no,
                supplier_batch_no: frm.doc.custom_supplier_batch_no,
                glue: frm.doc.custom_glue,
                pulp: frm.doc.custom_pulp,
                fsc: frm.doc.custom_fsc,
                lusture: frm.doc.custom_lusture,
                grade: frm.doc.custom_grade,
                cone: frm.doc.custom_cone,
                denier: frm.doc.custom_denier
            },
            callback: function(response) {
                if (response.message) {
                    var data = response.message;

                    // Check if the batch already exists in the child table
                    var exists = frm.doc.items.some(function(row) {
                        return row.batch_no === data.batch_no;  // Correct field name: 'batch_no'
                    });

                    if (!exists) {
                        // Add a new row to the table with the correct field names
                        let new_row = frm.add_child("items", {
                            item_code: data.item_code,
                            item_name: data.item_name,
                            qty: data.qty,
                            batch_no: data.batch_no,  // Correct field name
                            uom: data.uom,
                            custom_cone: data.cone,
                            custom_supplier_batch_no: data.supplier_batch_no,
                            custom_container_no: data.container_no,
                            custom_lot_no: data.lot_no,
                            use_serial_batch_fields: 1
                        });

                        frm.doc.items.unshift(frm.doc.items.pop());  // Reorder the table if needed
                        frm.refresh_field("items");
                        frm.set_value("custom_supplier_batch_no", "");
                        frm.refresh_field("custom_supplier_batch_no");
                        if (data.notes) {
                            frm.set_value("custom_notes", data.notes);
                        }

                        // Calculate totals after adding a new row
                        calculate_totals(frm);
                    } else {
                        frappe.msgprint(__('Batch already exists in the list.'));
                    }
                }
            }
        });
    }
}

