function fetch_and_append_batch(frm) {
    // Ensure both fields have values before making the call
    if (frm.doc.custom_supplier_batch_no) {
        frappe.call({
            method: "mhr.utilis.get_print_batch",
            args: {
                lot_no: frm.doc.custom_lot_no,
                container_no: frm.doc.custom_container_number,
                supplier_batch_no: frm.doc.custom_supplier_batch_no,
            },
            callback: function(response) {
                if (response.message) {
                    console.log(response.message);
                    var data = response.message;

                    // Check if the batch already exists in the child table
                    var exists = frm.doc.items.some(function(row) {
                        return row.batch_no === data.batch;
                    });

                    if (!exists) {
                        // Add a new row to the table
                        var childTable = frm.add_child("items");
                        childTable.item_code = data.item
                        childTable.batch_no = data.batch;
                        childTable.custom_cone = data.cone;
                        // childTable.batch_qty = data.batch_qty;

                        // Move the newly added row to the top of the table
                        // frm.doc.list_batches.unshift(frm.doc.list_batches.pop());

                        frm.refresh_field("items");
                        frm.set_value("custom_supplier_batch_no", "");  
                    } else {
                        frappe.msgprint(__('Batch already exists in the list.'));
                    }
                }
            }
        });
    }
}

