frappe.ui.form.on('Delivery Note', {
    refresh(frm) {
        // your code here
    },

    fetch_batches: function(frm) {
        frappe.call({
            method: "mhr.note.fetch_batches",
            args: {
                limit: frm.doc.count,
                lot_no: frm.doc.custom_lot_no,
                container_no: frm.doc.custom_container_no,
                glue: frm.doc.custom_glue,
                pulp: frm.doc.custom_pulp,
                fsc: frm.doc.custom_fsc,
                lusture: frm.doc.custom_lusture,
                grade: frm.doc.custom_grade,
                cone: frm.doc.custom_cone,
                denier: frm.doc.custom_denier,
            },
            callback: function(response) {
                if (response.message && response.message.length > 0) {
                    var batches = response.message;
                    var added_count = 0;
                    var skipped_count = 0;

                    // Loop through each batch in the response
                    batches.forEach(function(data) {
                        // Check if the batch already exists in the child table
                        var exists = frm.doc.items.some(function(row) {
                            return row.batch_no === data.name;  // 'name' is the batch_no in your response
                        });

                        if (!exists) {
                            // Add a new row to the table
                            frm.add_child("items", {
                                item_code: data.item,
                                item_name: data.item_name,
                                qty: data.batch_qty,
                                batch_no: data.name,
                                uom: data.stock_uom,
                                custom_cone: data.custom_cone,
                                custom_supplier_batch_no: data.custom_supplier_batch_no,
                                custom_container_no: data.custom_container_no,
                                custom_lot_no: data.custom_lot_no,
                                use_serial_batch_fields: 1
                            });
                            added_count++;
                            if (data.custom_notes && !frm.doc.custom_notes) {
                                frm.set_value("custom_notes", data.custom_notes);
                            }
                        } else {
                            skipped_count++;
                        }
                    });

                    // Refresh the child table after all items are added
                    frm.refresh_field("items");
                    frm.set_value("custom_supplier_batch_no", "");

                    // Calculate totals after adding rows
                    calculate_totals(frm);

                    // Show summary message
                    // if (added_count > 0) {
                    //     frappe.msgprint(__('Added {0} batch(es). {1} already existed.', [added_count, skipped_count]));
                    // } else {
                    //     frappe.msgprint(__('All {0} batches already exist in the list.', [skipped_count]));
                    // }
                } else {
                    frappe.msgprint(__('No batches found matching the criteria.'));
                }
            }
        });
    }
});
